package com.example.recyclerview;import android.app.Person;import android.content.Context;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.LinearLayout;import android.widget.TextView;import android.widget.Toast;import androidx.annotation.NonNull;import androidx.recyclerview.widget.RecyclerView;import java.util.ArrayList;public class MyAdapter1 extends RecyclerView.Adapter<MyAdapter1.MyHolder> {    Context ctx;    ArrayList<student> st;    public MyAdapter1(Context ctx, ArrayList<student> st) {        this.ctx = ctx;        this.st = st;    }    @NonNull    @Override    public MyHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        LayoutInflater li= LayoutInflater.from(ctx);        View v1 = li.inflate(R.layout.individual,parent,false);        return new MyHolder(v1);    }    @Override    public void onBindViewHolder(@NonNull MyHolder holder, final int position) {        student p = st.get(position);        holder.iv1.setImageBitmap(p.getPhoto());        holder.tv1.setText(p.getName());        holder.tv2.setText((CharSequence) p.getDate());    }    @Override    public int getItemCount() {        return 0;    }    class MyHolder extends RecyclerView.ViewHolder    {        TextView tv1,tv2;        ImageView iv1;        LinearLayout ll;        public MyHolder(@NonNull View itemView)        {            super(itemView);            tv1=itemView.findViewById(R.id.textView1);            tv2=itemView.findViewById(R.id.textView2);            iv1=itemView.findViewById(R.id.picture);            ll=itemView.findViewById(R.id.layoutlinear);        }    }}